import { useState } from 'react';
import './styles.css';

const row = {
  display: 'flex',
};

export default function App() {
  const [player, setPlayer] = useState(1);
  const [chosenCells, setChosenCells] = useState([]);

  const handleClick = (index) => {
    setChosenCells([...chosenCells, index]);
  };

  const renderCells = Array.from({ length: 9 }).map((_, index) => (
    <button
      className={`GridButton ${chosenCells.includes(index) ? 'blue' : 'red'}`}
      onClick={handleClick(index)}
      key={index}
    ></button>
  ));

  return (
    <div className='App'>
      {chosenCells}
